import logo from './logo.svg';
import './App.css';
import cover from './assets/dazegxdCover.jpg';
import pauseIcon from './assets/pause.png';
import playIcon from './assets/play.png';
import backIcon from './assets/back.png';
import forwardIcon from './assets/forward.png';
import {useState} from "react";

function App() {
    const [isPlaying, setIsPlaying] = useState(false);

    const togglePlay = () => {
        setIsPlaying(!isPlaying);
    };
    return (
    <div className= "App">
        <MusicPlayer isPlaying={isPlaying} togglePlay={togglePlay} />
        {songScroller()}
    </div>
    );
}

function MusicPlayer({isPlaying, togglePlay}) {
    return (
        <div className = "musicPlayer">
            <div className = "imageDiv">
              <img className = "coverImage" src={cover} alt="cover" />
            </div>
            <div className = "controls">
                <button className={"iconButton"}><img src={backIcon} alt="back" /></button>
                <button className={"iconButton"}>
                    <img src={isPlaying ? pauseIcon : playIcon} onClick={togglePlay} alt="play/pause" />
                </button>
                <button className={"iconButton"}><img src={forwardIcon} alt="forward" /></button>
            </div>
        </div>
    )
}
function songScroller() {
    const songList = [
        {id : 0, songName: "Song 0", artistName: "Artist 0"},
        {id : 1, songName: "Song 1", artistName: "Artist 1"},
        {id : 2, songName: "Song 2", artistName: "Artist 2"},
        {id : 3, songName: "Song 3", artistName: "Artist 3"},
        {id : 4, songName: "Song 4", artistName: "Artist 4"},
        {id : 5, songName: "Song 5", artistName: "Artist 5"},
        {id : 6, songName: "Song 6", artistName: "Artist 6"},
        {id : 7, songName: "Song 7", artistName: "Artist 7"},
        {id : 8, songName: "Song 8", artistName: "Artist 8"},
        {id : 9, songName: "Song 9", artistName: "Artist 9"},
        {id : 10, songName: "Song 10", artistName: "Artist 10"},
        {id : 11, songName: "Song 11", artistName: "Artist 11"},
        {id : 12, songName: "Song 12", artistName: "Artist 12"},
        {id : 13, songName: "Song 13", artistName: "Artist 13"},
        {id : 14, songName: "Song 14", artistName: "Artist 14"},
        {id : 15, songName: "Song 15", artistName: "Artist 15"},
        {id : 16, songName: "Song 16", artistName: "Artist 16"},
        {id : 17, songName: "Song 17", artistName: "Artist 17"},
        {id : 18, songName: "Song 18", artistName: "Artist 18"},
        {id : 19, songName: "Song 19", artistName: "Artist 19"},
        {id : 20, songName: "Song 20", artistName: "Artist 20"},
    ];
    return (
        <div className="SongScroller">
        {songList.map((button) => (
            <button className={"songButton"} key ={button.id} onClick = {() => alert(`Clicked ${button.label}`)}>
                <span style ={{fontSize : '1rem' }}>{button.songName}</span>
                <br/>
                <span style ={{fontSize : '0.83rem' }}>{button.artistName}</span>
            </button>
            ))}
        </div>
    );
}
export default App;
