import './App.css';
import { useState } from 'react';

import dazegxdCover from "./assets/dazegxdCover.jpg";
import pinegroveCover from "./assets/pinegroveCover.jpg";
import backIcon from "./assets/back.png";
import pauseIcon from "./assets/pause.png";
import playIcon from "./assets/play.png";
import forwardIcon from "./assets/forward.png";

function App() {
    const songList = [
        {id: 0, songName: "Undefined", artistName: "Dazegxd", cover: dazegxdCover},
        {id: 1, songName: "Need2", artistName: "Pinegrove", cover: pinegroveCover},
    ];
    const [activeSong, setActiveSong] = useState(songList[0]);
    const [isPlaying, setIsPlaying] = useState(false);

    return (
        <div className= "App">
            <MusicPlayer
                isPlaying = {isPlaying}
                setIsPlaying = {setIsPlaying}
                activeSong = {activeSong}
            />
            <SongScroller
                activeSong = {activeSong}
                setActiveSong = {setActiveSong}
                songList = {songList}
                setIsPlaying={setIsPlaying}
            />
        </div>
    );
}

function MusicPlayer({isPlaying, setIsPlaying, activeSong}) {
    return (
        <div className="musicPlayer">
            <div className="imageDiv">
                <img className="coverImage" src={activeSong ? activeSong.cover : ""} alt="cover" />
            </div>

            <div className="controls">
                <button className="iconButton">
                    <img src={backIcon} alt="back" />
                </button>

                <button className="iconButton" onClick={() => setIsPlaying(!isPlaying)}>
                    <img src={isPlaying ? pauseIcon : playIcon} alt="play/pause" />
                </button>

                <button className="iconButton">
                    <img src={forwardIcon} alt="forward" />
                </button>
            </div>
        </div>
    );
}

function SongScroller({activeSong, setActiveSong, songList, setIsPlaying}) {
    const handleSongChange = async event => {
        setActiveSong(event.target.id);
        setIsPlaying(true);

        const data = {id : activeSong.id};
        const requestOptions = {
            method: "POST",
            headers: { "Content-Type" : "application/json" },
            body: JSON.stringify(data)
        };

        const response = await fetch("http://localhost:3000")
    }
    return (
        <div className="SongScroller">
            {songList.map(song => (
                <button
                    key={song.id}
                    className={`songButton ${activeSong?.id === song.id ? 'activeSong' : ''}`}
                    onClick={handleSongChange}
                >
                    <span style={{fontSize: '1rem'}}>{song.songName}</span>
                    <br/>
                    <span style={{fontSize: '0.83rem'}}>{song.artistName}</span>
                </button>
            ))}
        </div>
    );
}

export default App;
